import { animateScroll as scroll } from 'react-scroll'
import styled from 'styled-components'

function isMobile() {
    return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
}

function transition() {
    window.onload = function () {
        setTimeout(function () {
            scroll.scrollTo(1000);
        }, 5000);
    }
}

let LandingCredit = styled("div")`
position: absolute;
width: 156px;
height: 47px;
left: 1275px;
top: 893px;

font-style: normal;
font-weight: 500;
font-size: 16px;
line-height: 19px;
display: flex;
align-items: center;
text-align: right;

color: #000000;
`;

if (isMobile()) {
LandingCredit = styled("div")`
position: absolute;
width: 132px;
height: 17px;
left: 240px;
top: 272px;

font-family: 'Sofia Sans';
font-style: normal;
font-weight: 500;
font-size: 9px;
line-height: 11px;
display: flex;
align-items: center;
text-align: right;

color: #000000;
`;
}


const LandingImage = (props) => {
    transition();
    return (
        <div>
            <img src={props.landing_image} alt="Landing Image" />
            <p style="position: absolute">{props.landing_credits}</p>
            <LandingCredit>
            </LandingCredit>
        </div>
    )
}

export default LandingImage;
